#pragma config(Sensor, dgtl1,  button,         sensorTouch)
#pragma config(Sensor, dgtl2,  top,            sensorTouch)
#pragma config(Motor,  port2,           left,          tmotorVex393HighSpeed_MC29, openLoop)
#pragma config(Motor,  port3,           right,         tmotorVex393HighSpeed_MC29, openLoop, reversed)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

task main()
{
	while (true)
	{
		while (!SensorValue[button]) sleep(10);
		unsigned long time = nPgmTime;
		motor[left] = motor[right] = 127;
		while (!SensorValue[top]) sleep(10);
		motor[left] = motor[right] = 0;
		unsigned long diff = nPgmTime - time;
		writeDebugStreamLine("Time: %d.%d s", diff / 1000, diff % 1000);
		while (SensorValue[button]) sleep(10);
	}
}
